import logo from './logo.svg';
import './App.css';

import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
//import * as parkData from "./capitalCities.json";
import Ciudades from "./capitalCities.json";

function App() {
  let puntuacion = 1500;
  return (
    <div>
      <header>

        <div className="navbar navbar-dark bg-dark shadow-sm">
          <div className="container">
            <a href="#" className="navbar-brand d-flex align-items-center">
              <strong>Cities Quiz</strong>
            </a>
          </div>
        </div>

      </header>

      <main>

        <section className="py-5 text-center container">
          <div className="row align-items-start">
            <div className="col-md-6 col-lg-2">
              <h6>Puntuación: {puntuacion}</h6>
              <table class="table">
                <thead>
                  <tr>
                    <th scope="col">#</th>
                    <th scope="col">City</th>
                    <th scope="col"></th>
                  </tr>
                </thead>
                <tbody>

                  {Ciudades.capitalCities.map((ciudad,index) => (
                    <tr id={index}>
                      <th scope="row">{index}</th>
                      <td>{ciudad.capitalCity}</td>
                      <td>⚪</td>
                    </tr>
                  ))}
                </tbody>
              </table>

            </div >
            <div className="col"> 

              <MapContainer center={[50.99298388467372, 10.78845445118975]} zoom={4}scrollWheelZoom={false}>
               <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                  />

                {Ciudades.capitalCities.map((ciudad,index) => (
                  <Marker
                    key={index}
                    position={[
                      ciudad.lat,
                      ciudad.long
                    ]}
                    onClick={() => {
                      alert()
                      //setActivePark(ciudad);
                    }}
                  >
                  <Popup>
                    {ciudad.capitalCity}
                  </Popup>
                  </Marker>
                ))}
              </MapContainer>

            </div>
          </div>
        </section>

      </main>
    </div>
  );
}

export default App;
