import logo from './logo.svg';
import './App.css';

import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
//import * as parkData from "./capitalCities.json";
import Ciudades from "./capitalCities.json";
import { useMapEvents } from 'https://cdn.esm.sh/react-leaflet/hooks'


function MyComponent() {
  const map = useMapEvents('click', () => {
    map.setCenter([50.5, 30.5])
  })
  return null
}

function MyMapComponent() {
  return (
    <MapContainer center={[50.5, 30.5]} zoom={13}>
      <MyComponent />
    </MapContainer>
  )
}

let puntuacion = 1500;

function reset(){
  puntuacion = 1200;
}

function fallo(ciudad,posicion){
  puntuacion = puntuacion - (ciudad - posicion);
}

function acierto(){

}

function App() {

  return (
    <div>
      <header>

        <div className="navbar navbar-dark bg-dark shadow-sm">
          <div className="container">
            <a href="#" className="navbar-brand d-flex align-items-center">
              <strong>Cities Quiz</strong>
            </a>
          </div>
        </div>

      </header>

      <main>

        <section className="py-5 text-center container">
          <div className="row align-items-start">
            <div className="col-md-6 col-lg-2">

              <table className="table">
                <thead>
                  <tr>
                    <th scope="col">#</th>
                    <th scope="col">Ciudad</th>
                    <th scope="col">Estado</th>
                  </tr>
                </thead>
                <tbody>

                  {Ciudades.capitalCities.map((ciudad,index) => (
                    <tr key={index}>
                      <th scope="row">{index}</th>
                      <td>{ciudad.capitalCity}</td>
                      <td>⚪</td>
                    </tr>
                  ))}
                </tbody>
              </table>
              🔴
              🟢
              <h6>Points: <span className="badge rounded-pill text-bg-primary">{puntuacion}</span></h6>
              <div className="alert alert-success" role="alert">
                <p>¡Enhorabuena 😃!</p>
                <p>Has conseguido averiguar todas las posiciones de las ciudades!</p>
                <p>Tu puntación es de <b>{puntuacion}</b></p>
              </div>
              <div className="alert alert-danger" role="alert">
                <p>¡Has perdido! 🙁</p>
                <p>Puedes volver a intentarlo</p>
              </div>

              <button type="button" onClick={() => reset()} className="btn btn-secondary btn-sm">Volver a empezar</button>

            </div >
            <div className="col"> 

              <MapContainer center={[50.99298388467372, 10.78845445118975]} zoom={4}scrollWheelZoom={false}>
               <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                  />

                {Ciudades.capitalCities.map((ciudad,index) => (
                  <Marker
                    key={index}
                    position={[
                      ciudad.lat,
                      ciudad.long
                    ]}
                    eventHandlers={{
                      click: () => {
                        console.log('ok')
                      },
                    }}
                  >

                  </Marker>
                ))}
              </MapContainer>

            </div>
          </div>
        </section>

      </main>
    </div>
  );
}

export default App;
