import logo from './logo.svg';
import './App.css';
import {useState, useRef, useMemo, useCallback} from "react";
import { MapContainer, TileLayer, Marker, Popup, useMapEvents } from 'react-leaflet';
//import * as parkData from "./capitalCities.json";
import Ciudades from "./capitalCities.json";



function App() {

  const [ puntuacion, cambioPuntuacion ] = useState(1500)
  const [ acertados, cambioAcertados ] = useState(0)

  const reset= () => {
    cambioPuntuacion(1500)
    cambioAcertados(0)
  }

  function DraggableMarker() {
    const markerRef = useRef(null)
    const [position, setPosition] = useState([50.99298388467372, 10.78845445118975])
    const eventHandlers = useMemo(
      () => ({
        dragend() {
          const marker = markerRef.current
          if (marker != null) {
            setPosition(marker.getLatLng())
            comprobar(marker.getLatLng())
          }
        },
      }),
      [],
    )

    return (
      <Marker
        draggable={true}
        eventHandlers={eventHandlers}
        position={position}
        ref={markerRef}>
      </Marker>
    )
  }

  const comprobar= (valor) => { 
    var ciudadlatlong = {
      'lat': Ciudades.capitalCities[acertados].lat,
      'lng':Ciudades.capitalCities[acertados].long
    };
    var dis = valor.distanceTo(ciudadlatlong);

    if(acertados < Ciudades.capitalCities.length && puntuacion > 0){
      if(dis <= 50 ){
        cambioAcertados(acertados+1);
      }
      else{
        if(puntuacion - dis < 0 ){
          cambioPuntuacion(0)
        }else{
          cambioPuntuacion(puntuacion - dis)
        }

      }
    }
  }

  return (
    <div>
      <header>

        <div className="navbar navbar-dark bg-dark shadow-sm">
          <div className="container">
            <a href="#" className="navbar-brand d-flex align-items-center">
              <strong>Cities Quiz</strong>
            </a>
          </div>
        </div>

      </header>

      <main>

        <section className="py-5 text-center container">
          <div className="row align-items-start">
            <div className="col-md-6 col-lg-3">
              { acertados > 0 ?
                (<span className="badge text-bg-success"><span>{acertados}</span> ciudades acertadas</span>
                ) : ""
              }

              <h5>Selecciona en el mapa la localización de: <b>{Ciudades.capitalCities[acertados].capitalCity}</b></h5>

              <button type="button" className="btn btn-primary">Comprobar ubicación</button>

              <h6 className="mt-6">Puntuación: <span className="badge rounded-pill text-bg-primary">{puntuacion}</span></h6>

              {puntuacion != 0 && acertados === Ciudades.capitalCities.length ?
                (<div className="alert alert-success" role="alert">
                  <p>¡Enhorabuena 😃!</p>
                  <p>Has conseguido averiguar todas las posiciones de las ciudades!</p>
                  <p>Tu puntación es de <b>{puntuacion}</b></p>
                </div>) : ""
              }

              {puntuacion == 0 ?
                (<div className="alert alert-danger" role="alert">
                  <p>¡Has perdido! 🙁</p>
                  <p>Puedes volver a intentarlo</p>
                </div>) : ""
              }

              <button type="button" onClick={() => reset()} className="btn btn-secondary btn-sm">Volver a empezar</button>

              <div className="mt-5 list">
                <h5>Listado de ciudades</h5>

                <table className="table">
                  <thead>
                    <tr>
                      <th scope="col">#</th>
                      <th scope="col">Ciudad</th>
                    </tr>
                  </thead>
                  <tbody>

                    {Ciudades.capitalCities.map((ciudad,index) => (
                      <tr key={index}>
                        <th scope="row">{index+1}</th>
                        <td>{ciudad.capitalCity}</td>
                      </tr>
                    ))}

                  </tbody>
                </table>
              </div>
            </div >
            <div className="col"> 

              <MapContainer center={[50.99298388467372, 10.78845445118975]} zoom={4}scrollWheelZoom={false}>
               <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                  />

                <DraggableMarker />

              </MapContainer>

            </div>
          </div>

        </section>

      </main>
    </div>
  );
}

export default App;
